database.action:
SHOW TABLES LIKE '{{PREFIX}}modules'
CREATE TABLE `{{PREFIX}}modules` ( `module_id` VARCHAR(32) NOT NULL, `module_name` VARCHAR(255) NOT NULL, `module_description` TEXT NULL, `module_author` VARCHAR(255) NULL, `module_version` VARCHAR(32) NOT NULL DEFAULT '', `module_priority` INT(10) UNSIGNED NOT NULL DEFAULT 0, `module_updatable` TINYINT(1) UNSIGNED NOT NULL DEFAULT 1, `module_status` TINYINT(1) UNSIGNED NOT NULL DEFAULT 0,
PRIMARY KEY (`module_id`), INDEX `{{PREFIX}}modules_status_idx` (`module_status`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;
