SHOW TABLES LIKE '{{PREFIX}}core_nav_menus'
CREATE TABLE `{{PREFIX}}core_nav_menus` ( `id` SERIAL, `title` VARCHAR(255) NOT NULL DEFAULT '', `active` TINYINT(1) UNSIGNED NOT NULL,
PRIMARY KEY (`id`), KEY `{{PREFIX}}core_nav_menus_items_idx_active` (`active`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;

SHOW TABLES LIKE '{{PREFIX}}core_nav_items'
CREATE TABLE `{{PREFIX}}core_nav_items` ( `id` SERIAL, `menu_id` BIGINT(20) UNSIGNED NOT NULL, `active` TINYINT(1) UNSIGNED NOT NULL, `ord` BIGINT(20) UNSIGNED NOT NULL,
PRIMARY KEY (`id`), KEY `{{PREFIX}}core_nav_items_idx_menu_id` (`menu_id`), KEY `{{PREFIX}}core_nav_items_idx_active` (`active`), KEY `{{PREFIX}}core_nav_items_idx_ord` (`ord`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;
