SHOW TABLES LIKE '{{PREFIX}}mod_menus'
CREATE TABLE `{{PREFIX}}mod_menus` ( `id` SERIAL, `active` TINYINT(1) UNSIGNED NOT NULL, `title` VARCHAR(255) NOT NULL DEFAULT '',
PRIMARY KEY (`id`), KEY `{{PREFIX}}mod_menus_idx_active` (`active`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;

SHOW TABLES LIKE '{{PREFIX}}mod_menus_items'
CREATE TABLE `{{PREFIX}}mod_menus_items` ( `id` SERIAL, `menu_id` BIGINT(20) UNSIGNED NOT NULL, `active` TINYINT(1) UNSIGNED NOT NULL, `ord` BIGINT(20) UNSIGNED NOT NULL,
PRIMARY KEY (`id`), KEY `{{PREFIX}}mod_menus_items_idx_menu_id` (`menu_id`), KEY `{{PREFIX}}mod_menus_items_idx_active` (`active`), KEY `{{PREFIX}}mod_menus_items_idx_ord` (`ord`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;
