SHOW TABLES LIKE '{{PREFIX}}mod_pages_permissions'

CREATE TABLE `{{PREFIX}}mod_pages_permissions` (
	`page_id` 			BIGINT(20) 	UNSIGNED 	NOT NULL DEFAULT 0,
	`group_id` 			BIGINT(20) 	UNSIGNED 	NOT NULL DEFAULT 0,

	PRIMARY KEY (`page_id`,`group_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;

SHOW TABLES LIKE '{{PREFIX}}mod_pages_categories'
CREATE TABLE `{{PREFIX}}mod_pages_categories` ( `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT, `active` TINYINT(1) UNSIGNED NOT NULL, `ord` INT(10) UNSIGNED NOT NULL, `parent_id` INT(10) UNSIGNED NOT NULL, `nleft` INT(10) UNSIGNED NOT NULL, `nright` INT(10) UNSIGNED NOT NULL, `level` INT(10) UNSIGNED NOT NULL, `tpl` VARCHAR(255) NULL, `items_tpl` VARCHAR(255) NULL,
PRIMARY KEY (`id`), KEY `{{PREFIX}}mod_pages_categories_idx_active` (`active`), KEY `{{PREFIX}}mod_pages_categories_idx_ord` (`ord`), KEY `{{PREFIX}}mod_pages_categories_idx_parent_id` (`parent_id`), KEY `{{PREFIX}}mod_pages_categories_idx_nright` (`nright`), KEY `{{PREFIX}}mod_pages_categories_idx_level` (`level`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE utf8_general_ci ;
